class Genghis_JsonResponse extends Genghis_Response
{
    public function renderHeaders()
    {
        $this->headers['Content-type']  = 'application/json';
        $this->headers['Cache-Control'] = 'no-cache, must-revalidate';
        $this->headers['Expires']       = 'Wed, 04 Aug 1982 00:00:00 GMT';

        parent::renderHeaders();
    }

    public function renderContent()
    {
        // json encoding a MongoId with PECL Mongo driver < 1.0.11 returns '{}' ...
        if (class_exists('Mongo') && version_compare(Mongo::VERSION, '1.0.11', '<')) {
            array_walk_recursive($this->data, array(__CLASS__, 'prepareData'), ini_get('mongo.cmd'));
        }

        print(json_encode($this->data));
    }

    private static function prepareData(&$data, $key, $cmd = '$')
    {
        if (is_object($data) && $data instanceof MongoId) {
            $data = array($cmd.'id' => (string) $data);
        }
    }
}
class Genghis_RedirectResponse extends Genghis_Response
{
    public function __construct($url, $status = 301)
    {
        parent::__construct($url, $status);
    }

    public function render()
    {
        header(sprintf('Location: %s', $this->data), $this->status);
    }
}
class Genghis_Response
{
    protected static $statusCodes = array(
        200 => 'OK',
        201 => 'Created',
        202 => 'Accepted',
        204 => 'No Content',
        301 => 'Moved Permanently',
        302 => 'Found',
        303 => 'See Other',
        304 => 'Not Modified',
        400 => 'Bad Request',
        401 => 'Unauthorized',
        403 => 'Forbidden',
        404 => 'Not Found',
        405 => 'Method Not Allowed',
        406 => 'Not Acceptable',
        412 => 'Precondition Failed',
        415 => 'Unsupported Media Type',
        417 => 'Expectation Failed',
        500 => 'Internal Server Error',
        501 => 'Not Implemented',
    );

    protected $data    = '';
    protected $status  = 200;
    protected $headers = array();

    public function __construct($data, $status = 200, $headers = array())
    {
        $this->data    = $data;
        $this->status  = $status;
        $this->headers = $headers;
    }

    public function render()
    {
        $this->renderHeaders();
        $this->renderContent();
    }

    public static function getStatusText($status)
    {
        if (isset(self::$statusCodes[$status])) {
            return self::$statusCodes[$status];
        }
    }

    protected function renderHeaders()
    {
        header(sprintf('HTTP/1.0 %s %s', $this->status, self::$statusCodes[$this->status]));
        foreach ($this->headers as $name => $val) {
            header(sprintf('%s: %s', $name, $val));
        }
    }

    protected function renderContent()
    {
        print((string) $this->data);
    }
}


