                }

                $property.append(
                    '<span class="e">' +
                    (isObject ? '{' : '[') +
                    ' <span class="m">' +
                    summary +
                    ' &hellip;</span> ' +
                    (isObject ? '}' : ']') +
                    '</span>'
                );
            }

            $property.toggleClass('collapsed');
            e.preventDefault();
        });
    }
};
